export class Terminal{constructor(t){this.element=t,this.lineList=[],this.typing=!1,this.mode="python"}printLine(t){this.lineList.push({content:t,type:"normal"}),this.typing||this.typeText()}printLineForce(t){this.lineList.push({content:t,type:"force"}),this.typing||this.typeText()}typeText(){if(0===this.lineList.length)return void(this.typing=!1);this.typing=!0;const{content:t,type:e}=this.lineList.shift();if("force"===e)this.appendContent(t),this.typeText();else{let e=0;const n=setInterval((()=>{e<t.length?(this.appendContent(t[e]),e++):(clearInterval(n),this.typeText())}),25)}}appendContent(t){const e=this.element.querySelector(".terminalText");e.querySelector("p").innerHTML+=t,e.scrollTop=e.scrollHeight}}function updateClock(){const t=new Date,e=new Date(t.getTime()+6e4*(180+t.getTimezoneOffset())),n=String(e.getHours()).padStart(2,"0"),i=String(e.getMinutes()).padStart(2,"0");for(const t of document.querySelectorAll("#clock"))t.textContent=`${n}:${i}`}updateClock(),setInterval(updateClock,6e3);
